def print(text):
    text = text.replace("[red]", "\033[31m").replace("[blue]", "\033[34m").replace("[green]", "\033[32m").replace("[yellow]", "\033[33m").replace("[white]", "\033[37m").replace("[reset]", "\033[0m").replace("[purple]", "\033[35m").replace("[cyan]", "\033[36m").replace("[black]", "\033[30m").replace("[underline]", "\033[4m").replace("[bold]", "\033[1m").replace("[italic]", "\033[3m").replace("[invert]", "\033[7m").replace("[strike]", "\033[9m").replace("[red background]", "\033[41m").replace("[blue background]", "\033[44m")
    text = text.replace("[green background]", "\033[42m").replace("[yellow background]", "\033[43m").replace("[white background]", "\033[47m").replace("[purple background]", "\033[45m").replace("[cyan background]", "\033[46m").replace("[black background]", "\033[40m").replace("[invert background]", "\033[7;4m").replace("[red underline]", "\033[4;31m").replace("[blue underline]", "\033[4;34m")
    text = text.replace("[green underline]", "\033[4;32m").replace("[yellow underline]", "\033[4;33m").replace("[white underline]", "\033[4;37m").replace("[purple underline]", "\033[4;35m").replace("[cyan underline]", "\033[4;36m").replace("[black underline]", "\033[4;30m")
    text = text.replace("[invert underline]", "\033[4;7m").replace("[red bold]", "\033[1;31m").replace("[blue bold]", "\033[1;34m").replace("[green bold]", "\033[1;32m").replace("[yellow bold]", "\033[1;33m").replace("[white bold]", "\033[1;37m").replace("[purple bold]", "\033[1;35m").replace("[cyan bold]", "\033[1;36m").replace("[black bold]", "\033[1;30m").replace("[invert bold]", "\033[1;7m").replace("[red italic]", "\033[3;31m").replace("[blue italic]", "\033[3;34m").replace("[green italic]", "\033[3;32m").replace("[yellow italic]", "\033[3;33m").replace("[white italic]", "\033[3;37m").replace("[purple italic]", "\033[3;35m").replace("[cyan italic]", "\033[3;36m").replace("[black italic]", "\033[3;30m")
    text = text.replace("[invert italic]", "\033[3;7m").replace("[red strike]", "\033[9;31m").replace("[blue strike]", "\033[9;34m").replace("[green strike]", "\033[9;32m").replace("[yellow strike]", "\033[9;33m").replace("[white strike]", "\033[9;37m").replace("[orange]", "\033[33m")
    print(text)